        expect = new int[][]{{-4, -2, 6}, {-4, 0, 4}, {-4, 1, 3}, {-4, 2, 2}, {-2, -2, 4}, {-2, 0, 2}};
        output = s.threeSum(nums);
        verifySortedInOut(expect, output, seq++);

